import React, { useState, useEffect } from "react";
import {
  Container,
  Grid,
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import axios from "axios";
import io from "socket.io-client";

function Dashboard({ user }) {
  const [referralTree, setReferralTree] = useState(null);
  const [earnings, setEarnings] = useState(null);
  const [purchaseData, setPurchaseData] = useState({
    amount: "",
    description: "",
  });
  const [error, setError] = useState("");
  const [notification, setNotification] = useState("");

  useEffect(() => {
    // Fetch initial data
    fetchReferralTree();
    fetchEarnings();

    // Setup socket connection
    const socket = io("http://localhost:3000", {
      query: { token: localStorage.getItem("token") },
    });

    socket.on("earningUpdate", (data) => {
      setNotification(
        `New earning of ${data.amount} from level ${data.level} referral!`
      );
      fetchEarnings(); // Refresh earnings
    });

    return () => socket.disconnect();
  }, []);

  const fetchReferralTree = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3000/analytics/referral-tree/${user.userId}`
      );
      setReferralTree(response.data);
    } catch (err) {
      setError("Failed to fetch referral tree");
    }
  };

  const fetchEarnings = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3000/analytics/earnings/${user.userId}`
      );
      setEarnings(response.data);
    } catch (err) {
      setError("Failed to fetch earnings");
    }
  };

  const handlePurchaseSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3000/purchases", {
        ...purchaseData,
        userId: user.userId,
      });
      setPurchaseData({ amount: "", description: "" });
      setError("");
    } catch (err) {
      setError(err.response?.data?.message || "Failed to record purchase");
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={3}>
        {/* User Info */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Your Referral Code: {user.referralCode}
            </Typography>
          </Paper>
        </Grid>

        {/* Purchase Form */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Record Purchase
            </Typography>
            <form onSubmit={handlePurchaseSubmit}>
              <TextField
                fullWidth
                label="Amount (Rs)"
                type="number"
                value={purchaseData.amount}
                onChange={(e) =>
                  setPurchaseData({ ...purchaseData, amount: e.target.value })
                }
                margin="normal"
                required
              />
              <TextField
                fullWidth
                label="Description"
                value={purchaseData.description}
                onChange={(e) =>
                  setPurchaseData({
                    ...purchaseData,
                    description: e.target.value,
                  })
                }
                margin="normal"
                required
              />
              <Button
                type="submit"
                variant="contained"
                color="primary"
                sx={{ mt: 2 }}
              >
                Record Purchase
              </Button>
            </form>
          </Paper>
        </Grid>

        {/* Earnings */}
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Your Earnings
            </Typography>
            {earnings && (
              <>
                <Typography variant="h4" color="primary">
                  Total: Rs. {earnings.totalEarnings}
                </Typography>
                <Typography variant="subtitle1">
                  Level 1 Earnings: Rs.{" "}
                  {earnings.level1Earnings.reduce(
                    (sum, e) => sum + e.amount,
                    0
                  )}
                </Typography>
                <Typography variant="subtitle1">
                  Level 2 Earnings: Rs.{" "}
                  {earnings.level2Earnings.reduce(
                    (sum, e) => sum + e.amount,
                    0
                  )}
                </Typography>
              </>
            )}
          </Paper>
        </Grid>

        {/* Referral Tree */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Your Referral Tree
            </Typography>
            {referralTree && (
              <Box sx={{ mt: 2 }}>
                <Typography variant="subtitle1" gutterBottom>
                  Direct Referrals ({referralTree.directReferrals.length}/8)
                </Typography>
                <Grid container spacing={2}>
                  {referralTree.directReferrals.map((referral) => (
                    <Grid item xs={12} sm={6} md={4} key={referral.userId}>
                      <Card>
                        <CardContent>
                          <Typography variant="h6">{referral.name}</Typography>
                          <Typography color="textSecondary">
                            Status: {referral.isActive ? "Active" : "Inactive"}
                          </Typography>
                          <Typography variant="body2">
                            Referrals: {referral.referrals.length}
                          </Typography>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            )}
          </Paper>
        </Grid>
      </Grid>

      {/* Notifications */}
      {notification && (
        <Paper
          sx={{
            position: "fixed",
            bottom: 20,
            right: 20,
            p: 2,
            bgcolor: "success.light",
            color: "white",
          }}
        >
          {notification}
        </Paper>
      )}

      {/* Error Messages */}
      {error && (
        <Paper
          sx={{
            position: "fixed",
            bottom: 20,
            right: 20,
            p: 2,
            bgcolor: "error.light",
            color: "white",
          }}
        >
          {error}
        </Paper>
      )}
    </Container>
  );
}

export default Dashboard;
